import { imageUrl } from "../utils/imageUrl.mjs";


const Picks = (props) => {
    const {picks, team} = props;
    const isRed = team === "red";
    const dividerStyle = `mb-2 h-px w-64 from-gray-500/50 to-gray-500/0 ${isRed? "bg-gradient-to-l" : "bg-gradient-to-r"}`;
    const isNullPick = (pick) => pick === null;
    return (
        <div > 
            <div className={dividerStyle}></div>          
            {picks.map((pick, index) =>(
                <div className={`flex flex-col ${isRed? "items-end": "items-start"}`}>
                    <div className={`flex items-center gap-3 ${isRed && "flex-row-reverse"}`}>
                        <div className="rounded-full border-2 border-yellow-400/70 border-solid h-14 w-14 bg-center bg-cover transition-all" key={index} style={{backgroundImage: isNullPick(pick)? "none" : `url(${imageUrl(pick)})`}}></div>
                        <p className="text-gray-200/70">{pick}</p>
                    </div>
                    
                    <div className={`${dividerStyle} mt-2`}></div>
                </div>
                
            ) 
            
            )}
        </div>
    )
}

export default Picks
