import {useState, useEffect} from 'react';
import Bans from './components/Bans.jsx';
import Champions from './components/Champions.jsx';
import Picks from './components/Picks.jsx';
import Undo from './components/Undo.jsx';
import getChampions from './utils/getChampions.mjs';

function App() {
    const phases = ['bb1', 'rb1', 'bb2', 'rb2', 'bb3', 'rb3', 'bp1', 'rp1', 'rp2', 'bp2', 'bp3', 'rp3', 'rb4', 'bb4', 'rb5', 'bb5', 'rp4', 'bp4', 'bp5', 'rp5'];
    const [champions, setChampions] = useState([]);
    const [bluePicks, setBluePicks] = useState([null, null, null, null, null]);
    const [redPicks, setRedPicks] = useState([null, null, null, null, null]);
    const [blueBans, setBlueBans] = useState([null, null, null, null, null]);
    const [redBans, setRedBans] = useState([null, null, null, null, null]);
    const [currentPhase, setCurrentPhase] = useState(0);

    const undo = () => {
        if (currentPhase > 0) {
            const phase = phases[currentPhase].slice(0,2);
            const index = parseInt(phases[currentPhase][2]);
            if (phase === 'bb') {
                setBlueBans(prev => [prev.slice(0, index), null, prev.slice(index)]);
            } else if (phase === 'rb') {
                setRedBans(prev => [prev.slice(0, index), null, prev.slice(index)]);
            } else if (phase === "bp") {
                setBluePicks(prev => [prev.slice(0, index), null, prev.slice(index)]);
            } else {
                setRedPicks(prev => [prev.slice(0, index), null, prev.slice(index)]);
            }

            setCurrentPhase(prev => prev - 1);
        }    
    }

    const selectChampion = e => {
        if (currentPhase < 19) {

            const champion = e.target.id;
            const phase = phases[currentPhase].slice(0,2);
            const index = parseInt(phases[currentPhase][2]);
            if (phase === 'bb') {
                setBlueBans(prev => [prev.slice(0, index), champion, prev.slice(index)]);
            } else if (phase === 'rb') {
                setRedBans(prev => [prev.slice(0, index), champion, prev.slice(index)]);
            } else if (phase === "bp") {
                setBluePicks(prev => [prev.slice(0, index), champion, prev.slice(index)]);
            } else {
                setRedPicks(prev => [prev.slice(0, index), champion, prev.slice(index)]);
            }

            setCurrentPhase(prev => prev + 1);
        }
    }

    useEffect(() => getChampions(setChampions), []);

    return (
        <div className="App">
            <Undo undo={undo}/>
            <Picks picks={bluePicks}/>
            <Bans bans={blueBans}/>
            <Champions selectChampion={selectChampion} champions={champions}/>
            <Picks picks={redPicks}/>
            <Bans bans={redBans} />
        </div>
    );
}

export default App;
