import { imageUrl } from "../utils/imageUrl.mjs";


const Picks = (props) => {
    const {picks, team} = props;
    
    return (
        <div className={`flex flex-col ${team === "blue"? "items-start": "items-end"}`}>           
            {picks.map((pick, index) => <div className="rounded-full border-2 border-yellow-400/70 border-solid h-14 w-14 bg-center bg-cover transition-all" key={index} style={{backgroundImage: pick === null? "none" : `url(${imageUrl(pick)})`}}></div>)}
        </div>
    )
}

export default Picks
