import { imageUrl } from "../utils/imageUrl.mjs";


const Picks = (props) => {
    const {picks, team} = props;
    const isBlue = team === "blue";
    const dividerStyle = `mb-2 h-px w-64 from-gray-500/50 to-gray-500/0 ${isBlue? "bg-gradient-to-r" : "bg-gradient-to-l"}`;
    return (
        <div > 
            <div className={dividerStyle}></div>          
            {picks.map((pick, index) =>(
                <div className={`flex flex-col ${isBlue? "items-start": "items-end"}`}>
                    <div className={`flex items-center`}>
                        <div className="rounded-full border-2 border-yellow-400/70 border-solid h-14 w-14 bg-center bg-cover transition-all" key={index} style={{backgroundImage: pick === null? "none" : `url(${imageUrl(pick)})`}}></div>
                        <p>{pick}</p>
                    </div>
                    
                    <div className={`${dividerStyle} mt-2`}></div>
                </div>
                
            ) 
            
            )}
        </div>
    )
}

export default Picks
