import { imageUrl } from "../utils/imageUrl.mjs";


const Champions = (props) => {
    const {champions, selectChampion, pickedChampions} = props;
    return (
        <div className="flex flex-wrap gap-0.5 justify-center">
            {champions.map((champion, index) => (
            <div onClick={selectChampion} key={index} id={champion} className="h-20 w-20 bg-cover" style={{backgroundImage: `url(${imageUrl(champion)})`}}></div>           
            ))}
        </div>
  )
}

export default Champions
