import { imageUrl } from "../utils/imageUrl.mjs";


const Bans = (props) => {
    const {bans} = props;
    const isThird = index => index === 2;
    return (
        <div className="flex gap-px">
            {bans.map((ban, index) => <div className={`border border-gray-500/50 h-8 w-8 bg-cover ${isThird(index) && "ml-2"}`} key={index} style={{backgroundImage: ban === null? "none" : `url(${imageUrl(ban)})`}}></div>)}
        </div>
    )
}

export default Bans
